import React, { useState } from 'react';
import { User } from 'lucide-react'; // Si tu utilises lucide-react pour l'icône
import { Link } from 'react-router-dom'; // Si tu utilises react-router pour la navigation

const UserDropdown = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Gérer l'authentification
  const handleLogout = () => {
    setIsAuthenticated(false); // Simulation de la déconnexion
    // Ajoute ici la logique réelle pour déconnecter l'utilisateur
  };

  return (
    <div className="relative">
      <a 
        href="/user" 
        className="p-2 rounded-full hover:bg-gray-700" 
        onClick={() => setIsOpen(!isOpen)} 
      >
        <User className="h-5 w-5" />
      </a>

      {isOpen && (
        <div className="absolute right-0 mt-2 w-48 bg-gray-800 text-white rounded-lg shadow-lg p-3">
          <div className="space-y-2">
            {isAuthenticated ? (
              <>
                <Link to="/profile" className="block hover:bg-gray-700 p-2 rounded">Mon Profil</Link>
                <button 
                  onClick={handleLogout} 
                  className="block w-full text-left hover:bg-gray-700 p-2 rounded"
                >
                  Se déconnecter
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="block hover:bg-gray-700 p-2 rounded">Connexion</Link>
                <Link to="/register" className="block hover:bg-gray-700 p-2 rounded">S'inscrire</Link>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDropdown;
