import React, { useState, useEffect } from 'react';
import { CreditCard } from 'lucide-react';
import AppLayout from './app-layout';
import { useLocation } from 'react-router-dom';

const OrderForm = () => {
  const location = useLocation();
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    postalCode: '',
    country: 'FR',
    creditCardNumber: '',
    creditCardExpiry: '',
    creditCardCVC: '',
  });

  const orders = location.state?.cart || [];

  // Charger les données depuis le localStorage
  useEffect(() => {
    const savedData = localStorage.getItem('orderFormData');
    if (savedData) {
      setFormData(JSON.parse(savedData));
    }
  }, []);

  // Sauvegarder automatiquement les données dans le localStorage à chaque mise à jour
  useEffect(() => {
    localStorage.setItem('orderFormData', JSON.stringify(formData));
  }, [formData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Commande soumise:', formData);
    // Vous pouvez ajouter une logique ici pour envoyer les données au backend
  };

  return (
    <AppLayout>
      <div className="max-w-4xl mx-auto space-y-8">
        <h2 className="text-2xl font-bold">Finaliser votre commande</h2>

        {/* Liste des commandes */}
        <div className="bg-gray-800 rounded-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Vos commandes</h3>
          {orders.length > 0 ? (
            <ul className="space-y-2">
              {orders.map((vehicle, index) => (
                <li key={index} className="flex justify-between items-center">
                  <span>{vehicle.name}</span>
                  <span>{vehicle.price.toLocaleString()} €</span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Aucune commande ajoutée.</p>
          )}
        </div>

        {/* Formulaire */}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Informations personnelles */}
          <div className="bg-gray-800 rounded-lg p-6">
            <h3 className="text-lg font-semibold mb-4">Informations personnelles</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium mb-1">Prénom</label>
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border-gray-600 rounded-md"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Nom</label>
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border-gray-600 rounded-md"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border-gray-600 rounded-md"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Téléphone</label>
                <input
                  type="text"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="w-full bg-gray-700 border-gray-600 rounded-md"
                  required
                />
              </div>
            </div>
          </div>

          {/* Méthode de paiement */}
          <div className="bg-gray-800 rounded-lg p-6">
            <h3 className="text-lg font-semibold mb-4">Méthode de paiement</h3>
            <div className="flex items-center space-x-6">
              <div className="flex items-center">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="creditCard"
                  checked={paymentMethod === 'creditCard'}
                  onChange={() => setPaymentMethod('creditCard')}
                  className="mr-2"
                />
                <CreditCard className="h-5 w-5 text-white" />
                <span className="ml-2 text-white">Carte de crédit</span>
              </div>
              <div className="flex items-center">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="cash"
                  checked={paymentMethod === 'cash'}
                  onChange={() => setPaymentMethod('cash')}
                  className="mr-2"
                />
                <span className="ml-2 text-white">Paiement à la livraison</span>
              </div>
            </div>
          </div>

          <button type="submit" className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700">
            Finaliser la commande
          </button>
        </form>
      </div>
    </AppLayout>
  );
};

export default OrderForm;
