import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../components/AuthProvider'; // Assurez-vous que le chemin est correct

const PrivateRoute = ({ children }) => {
  const { user } = useAuth();

  if (user === undefined) {
    // Ajouter un écran de chargement pendant l'initialisation
    return <div>Chargement...</div>;
  }

  if (!user) {
    return <Navigate to="/login" />;
  }

  return children;
};
