import React, { useState } from 'react';
import { Search, Grid, List, ShoppingCart } from 'lucide-react';
import AppLayout from './app-layout';
import { Link } from 'react-router-dom';

const CataloguePage = () => {
  const [viewMode, setViewMode] = useState('grid');
  const [searchQuery, setSearchQuery] = useState('');
  const [cart, setCart] = useState([]);

  const vehicles = [
    {
      id: 1,
      name: 'Tesla Model 3',
      type: 'automobile',
      energy: 'electric',
      price: 42900,
      image: '/api/placeholder/400/300',
      description: 'Berline électrique performante',
    },
    {
      id: 2,
      name: 'Vespa Elettrica',
      type: 'scooter',
      energy: 'electric',
      price: 7499,
      image: '/api/placeholder/400/300',
      description: 'Scooter électrique urbain',
    },
  ];

  const addToCart = (vehicle) => {
    setCart((prevCart) => [...prevCart, vehicle]);
    alert(`${vehicle.name} a été ajouté au panier !`);
  };

  const filteredVehicles = vehicles.filter((vehicle) =>
    vehicle.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const VehicleCard = ({ vehicle, isList }) => {
    return (
      <div className={`bg-gray-800 rounded-lg shadow-lg hover:shadow-xl transform transition-all duration-300 ${isList ? 'flex' : ''}`}>
        <img
          src={vehicle.image}
          alt={vehicle.name}
          className={`object-cover ${isList ? 'w-48 h-48' : 'w-full h-64'}`}
        />
        <div className={`p-6 ${isList ? 'flex-1' : ''}`}>
          <h3 className="text-xl font-semibold mb-3">{vehicle.name}</h3>
          <p className="text-gray-300 mb-4">{vehicle.description}</p>
          <div className="flex justify-between items-center">
            <span className="text-blue-400 font-bold text-lg">{vehicle.price.toLocaleString()} €</span>
            <button
              onClick={() => addToCart(vehicle)}
              className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-all duration-300"
            >
              <ShoppingCart className="h-5 w-5" />
              Ajouter au panier
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <AppLayout>
      <div className="flex flex-col space-y-6">
        <div className="flex flex-col md:flex-row gap-4 items-center">
          <div className="relative flex-1">
            <input
              type="text"
              placeholder="Rechercher un véhicule..."
              className="w-full pl-10 pr-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:ring-blue-500 focus:border-blue-500"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Search className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 rounded-md ${viewMode === 'grid' ? 'bg-blue-600' : 'bg-gray-700'}`}
            >
              <Grid className="h-5 w-5" />
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 rounded-md ${viewMode === 'list' ? 'bg-blue-600' : 'bg-gray-700'}`}
            >
              <List className="h-5 w-5" />
            </button>
          </div>
        </div>

        <div className={`grid gap-6 ${viewMode === 'grid' ? 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3' : 'grid-cols-1'}`}>
          {filteredVehicles.map((vehicle) => (
            <VehicleCard key={vehicle.id} vehicle={vehicle} isList={viewMode === 'list'} />
          ))}
        </div>
      </div>
    </AppLayout>
  );
};

export default CataloguePage;
