import React, { useState, useEffect } from 'react';
import { useAuth } from './AuthProvider'; // Assurez-vous d'importer correctement le hook d'authentification
import AppLayout from './app-layout'; // Assurez-vous que le chemin est correct
import { 
  Users, Package, ShoppingCart, Settings, 
  Plus, Edit, Trash2, Search, FileText 
} from 'lucide-react';
import { useHistory } from 'react-router-dom'; // Pour gérer la redirection

const AdminDashboard = () => {
  const { user } = useAuth(); // Accéder à l'utilisateur connecté
  const history = useHistory(); // Pour rediriger l'utilisateur si nécessaire
  const [activeTab, setActiveTab] = useState('vehicles');

  // Vérifier si l'utilisateur est un admin, sinon rediriger
  useEffect(() => {
    if (!user || user.role !== 'admin') {
      history.push('/'); // Rediriger vers la page d'accueil ou une page d'erreur si non admin
    }
  }, [user, history]);

  const [vehicles, setVehicles] = useState([]); // Pour stocker les véhicules
  const [orders, setOrders] = useState([]); // Pour stocker les commandes
  const [clients, setClients] = useState([]); // Pour stocker les clients

  return (
    <AppLayout>
      <div className="min-h-screen bg-gray-900">
        <div className="flex">
          {/* Sidebar */}
          <div className="w-64 bg-gray-800 min-h-screen p-4">
            <h2 className="text-xl font-bold text-blue-400 mb-6">Administration</h2>
            <nav className="space-y-2">
              <button
                onClick={() => setActiveTab('vehicles')}
                className={`w-full flex items-center space-x-2 px-4 py-2 rounded-lg ${
                  activeTab === 'vehicles' ? 'bg-blue-600' : 'hover:bg-gray-700'
                }`}
              >
                <Package className="h-5 w-5" />
                <span>Véhicules</span>
              </button>
              <button
                onClick={() => setActiveTab('orders')}
                className={`w-full flex items-center space-x-2 px-4 py-2 rounded-lg ${
                  activeTab === 'orders' ? 'bg-blue-600' : 'hover:bg-gray-700'
                }`}
              >
                <ShoppingCart className="h-5 w-5" />
                <span>Commandes</span>
              </button>
              <button
                onClick={() => setActiveTab('clients')}
                className={`w-full flex items-center space-x-2 px-4 py-2 rounded-lg ${
                  activeTab === 'clients' ? 'bg-blue-600' : 'hover:bg-gray-700'
                }`}
              >
                <Users className="h-5 w-5" />
                <span>Clients</span>
              </button>
              <button
                onClick={() => setActiveTab('settings')}
                className={`w-full flex items-center space-x-2 px-4 py-2 rounded-lg ${
                  activeTab === 'settings' ? 'bg-blue-600' : 'hover:bg-gray-700'
                }`}
              >
                <Settings className="h-5 w-5" />
                <span>Paramètres</span>
              </button>
            </nav>
          </div>

          {/* Main Content */}
          <div className="flex-1 p-6">
            {activeTab === 'vehicles' && (
              <div className="space-y-6">
                <div className="flex justify-between items-center">
                  <h2 className="text-2xl font-bold">Gestion des Véhicules</h2>
                  <button className="flex items-center px-4 py-2 bg-blue-600 rounded-lg hover:bg-blue-700">
                    <Plus className="h-5 w-5 mr-2" />
                    Ajouter un véhicule
                  </button>
                </div>

                {/* Search and Filters */}
                <div className="flex gap-4">
                  <div className="relative flex-1">
                    <input
                      type="text"
                      placeholder="Rechercher un véhicule..."
                      className="w-full pl-10 pr-4 py-2 bg-gray-800 border border-gray-700 rounded-lg"
                    />
                    <Search className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                  </div>
                  <select className="bg-gray-800 border border-gray-700 rounded-lg px-4">
                    <option value="all">Tous les types</option>
                    <option value="car">Automobiles</option>
                    <option value="scooter">Scooters</option>
                  </select>
                </div>

                {/* Vehicles Table */}
                <div className="bg-gray-800 rounded-lg overflow-hidden">
                  <table className="w-full">
                    <thead>
                      <tr className="bg-gray-700">
                        <th className="px-6 py-3 text-left">ID</th>
                        <th className="px-6 py-3 text-left">Nom</th>
                        <th className="px-6 py-3 text-left">Type</th>
                        <th className="px-6 py-3 text-left">Prix</th>
                        <th className="px-6 py-3 text-left">Stock</th>
                        <th className="px-6 py-3 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-700">
                      {/* Remplacer par les données de l'API */}
                      <tr>
                        <td className="px-6 py-4">001</td>
                        <td className="px-6 py-4">Tesla Model 3</td>
                        <td className="px-6 py-4">Automobile</td>
                        <td className="px-6 py-4">42,900 €</td>
                        <td className="px-6 py-4">5</td>
                        <td className="px-6 py-4">
                          <div className="flex space-x-2">
                            <button className="p-2 hover:bg-gray-700 rounded">
                              <Edit className="h-4 w-4" />
                            </button>
                            <button className="p-2 hover:bg-gray-700 rounded">
                              <Trash2 className="h-4 w-4" />
                            </button>
                            <button className="p-2 hover:bg-gray-700 rounded">
                              <FileText className="h-4 w-4" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            {activeTab === 'orders' && (
              <div className="space-y-6">
                <h2 className="text-2xl font-bold">Gestion des Commandes</h2>
                {/* Implémenter la gestion des commandes */}
              </div>
            )}

            {activeTab === 'clients' && (
              <div className="space-y-6">
                <h2 className="text-2xl font-bold">Gestion des Clients</h2>
                {/* Implémenter la gestion des clients */}
              </div>
            )}

            {activeTab === 'settings' && (
              <div className="space-y-6">
                <h2 className="text-2xl font-bold">Paramètres</h2>
                {/* Implémenter les paramètres */}
              </div>
            )}
          </div>
        </div>
      </div>
    </AppLayout>
  );
};

export default AdminDashboard;
