import React, { useState } from 'react';
import { Trash2, ChevronLeft, ChevronRight } from 'lucide-react';

import AppLayout from './app-layout'; // Assurez-vous que le chemin est correct

const ShoppingCart = () => {
  const [cart, setCart] = useState([]);
  const [order, setOrder] = useState([]);

  const products = [
    {
      id: 1,
      name: "Tesla Model 3",
      price: 42900,
      options: ["Autopilot", "Intérieur premium"],
      image: "/api/placeholder/400/300"
    },
    {
      id: 2,
      name: "iPhone 13",
      price: 999,
      options: ["128 Go", "Couleur bleue"],
      image: "/api/placeholder/400/300"
    }
  ];

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const addItemToCart = (product) => {
    const itemExists = cart.find(item => item.id === product.id);
    if (itemExists) {
      // Si l'élément existe déjà, on augmente sa quantité
      setCart(cart.map(item => 
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      // Sinon, on ajoute un nouvel élément au panier
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeItemFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const updateQuantity = (id, newQuantity) => {
    if (newQuantity < 1) return; // Empêcher d'avoir une quantité inférieure à 1
    setCart(cart.map(item => 
      item.id === id ? { ...item, quantity: newQuantity } : item
    ));
  };

  const moveToOrder = () => {
    setOrder([...cart]); // Déplacer les articles du panier vers la commande
    setCart([]); // Vider le panier après la commande
  };

  return (
    <AppLayout>
      <div className="bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <h2 className="text-2xl font-bold mb-6 text-white">Votre Panier</h2>

          {/* Liste des produits disponibles */}
          <div className="space-y-4">
            <h3 className="text-xl font-semibold text-white">Produits disponibles</h3>
            {products.map(product => (
              <div key={product.id} className="flex items-center justify-between bg-gray-800 p-4 rounded-md">
                <div className="flex items-center space-x-4">
                  <img src={product.image} alt={product.name} className="w-24 h-24 object-cover rounded-md" />
                  <div>
                    <h3 className="text-lg font-semibold text-white">{product.name}</h3>
                    <div className="text-sm text-gray-400">Options: {product.options.join(", ")}</div>
                    <div className="text-white mt-2">{product.price.toLocaleString()} €</div>
                  </div>
                </div>
                <button
                  onClick={() => addItemToCart(product)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md"
                >
                  Ajouter au panier
                </button>
              </div>
            ))}
          </div>

          {/* Panier */}
          {cart.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-400 mb-4">Votre panier est vide</p>
              <a
                href="/catalogue"
                className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
              >
                <ChevronLeft className="mr-2 h-5 w-5" />
                Continuer vos achats
              </a>
            </div>
          ) : (
            <div className="space-y-1">
              {/* Liste des articles dans le panier */}
              <div className="bg-gray-800 rounded-lg overflow-hidden shadow-md">
                {cart.map((item) => (
                  <div key={item.id} className="p-6 border-b border-gray-700 last:border-0">
                    <div className="flex items-center space-x-6">
                      <img
                        src={item.image}
                        alt={item.name}
                        className="w-24 h-24 object-cover rounded-md"
                      />
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-white">{item.name}</h3>
                        <div className="text-sm text-gray-400 mt-1">
                          Options: {item.options.join(", ")}
                        </div>
                        <div className="mt-4 flex items-center justify-between">
                          {/* Contrôles de quantité */}
                          <div className="flex items-center space-x-4">
                            <button
                              onClick={() => updateQuantity(item.id, item.quantity - 1)}
                              className="p-1 rounded-md hover:bg-gray-700 text-white"
                            >
                              <ChevronLeft className="h-5 w-5" />
                            </button>
                            <span className="text-white">{item.quantity}</span>
                            <button
                              onClick={() => updateQuantity(item.id, item.quantity + 1)}
                              className="p-1 rounded-md hover:bg-gray-700 text-white"
                            >
                              <ChevronRight className="h-5 w-5" />
                            </button>
                          </div>

                          {/* Prix et suppression */}
                          <div className="flex items-center space-x-4">
                            <span className="text-blue-400 font-bold">
                              {(item.price * item.quantity).toLocaleString()} €
                            </span>
                            <button
                              onClick={() => removeItemFromCart(item.id)}
                              className="p-2 text-red-400 hover:bg-gray-700 rounded-md"
                            >
                              <Trash2 className="h-5 w-5" />
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Résumé de la commande */}
              <div className="bg-gray-800 rounded-lg p-6 mt-6 shadow-md">
                <h3 className="text-lg font-semibold text-white mb-4">Résumé de la commande</h3>
                <div className="space-y-2 text-white">
                  <div className="flex justify-between">
                    <span>Sous-total</span>
                    <span>{calculateTotal().toLocaleString()} €</span>
                  </div>
                  <div className="flex justify-between">
                    <span>TVA (20%)</span>
                    <span>{(calculateTotal() * 0.2).toLocaleString()} €</span>
                  </div>
                  <div className="border-t border-gray-700 pt-2 mt-2">
                    <div className="flex justify-between font-bold">
                      <span>Total</span>
                      <span className="text-blue-400">
                        {(calculateTotal() * 1.2).toLocaleString()} €
                      </span>
                    </div>
                  </div>
                </div>
                <div className="mt-6">
                  <button
                    onClick={moveToOrder}
                    className="w-full block text-center px-6 py-3 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                  >
                    Finaliser la commande
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Commande Finalisée */}
          {order.length > 0 && (
            <div className="mt-12 bg-gray-800 p-6 rounded-lg shadow-md">
              <h3 className="text-xl font-semibold text-white mb-6">Commande Finalisée</h3>
              <div className="space-y-4">
                {order.map(item => (
                  <div key={item.id} className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <img src={item.image} alt={item.name} className="w-24 h-24 object-cover rounded-md" />
                      <div>
                        <h4 className="text-lg font-semibold text-white">{item.name}</h4>
                        <div className="text-sm text-gray-400">Options: {item.options.join(", ")}</div>
                      </div>
                    </div>
                    <div className="text-blue-400 font-bold">
                      {(item.price * item.quantity).toLocaleString()} €
                    </div>
                  </div>
                ))}
                <div className="border-t border-gray-700 mt-6 pt-4">
                  <div className="flex justify-between font-bold">
                    <span>Total de la commande</span>
                    <span className="text-blue-400">
                      {(calculateTotal() * 1.2).toLocaleString()} €
                    </span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </AppLayout>
  );
};

export default ShoppingCart;
