import React, { useState, useEffect } from 'react';
import AppLayout from './app-layout';
import OrderManagement from './order-management';
import ClientManagement from './client-management';
import VehicleForm from './vehicle-form';
import { 
  Users, Package, ShoppingCart, Settings, 
  Plus, Edit, Trash2, Search, FileText 
} from 'lucide-react';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('vehicles');
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentVehicle, setCurrentVehicle] = useState(null);
  const [vehicles, setVehicles] = useState([]);
  const [filteredVehicles, setFilteredVehicles] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [vehicleTypeFilter, setVehicleTypeFilter] = useState('all');
  const [orders, setOrders] = useState([]);
  const [clients, setClients] = useState([]);

  // Mettre à jour les véhicules filtrés chaque fois que vehicles, searchQuery ou vehicleTypeFilter change
  useEffect(() => {
    filterVehicles();
  }, [vehicles, searchQuery, vehicleTypeFilter]);

  const handleAddVehicleClick = () => {
    setCurrentVehicle(null);
    setIsEditing(false);
    setIsFormOpen(true);
  };

  const handleEditVehicleClick = (vehicle) => {
    setCurrentVehicle(vehicle);
    setIsEditing(true);
    setIsFormOpen(true);
  };

  const handleFormClose = () => {
    setIsFormOpen(false);
    setCurrentVehicle(null);
    setIsEditing(false);
  };

  const handleFormSubmit = (vehicleData) => {
    if (isEditing && currentVehicle) {
      // Mise à jour d'un véhicule existant
      setVehicles(vehicles.map(vehicle =>
        vehicle.id === currentVehicle.id ? { ...vehicleData, id: currentVehicle.id } : vehicle
      ));
    } else {
      // Ajout d'un nouveau véhicule avec un ID unique
      const newVehicle = {
        ...vehicleData,
        id: Date.now().toString() // Génère un ID unique basé sur le timestamp
      };
      setVehicles(prevVehicles => [...prevVehicles, newVehicle]);
    }
    handleFormClose();
  };

  const handleDeleteVehicle = (vehicleId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce véhicule ?')) {
      setVehicles(vehicles.filter(vehicle => vehicle.id !== vehicleId));
    }
  };

  const filterVehicles = () => {
    let filtered = [...vehicles];

    if (searchQuery) {
      filtered = filtered.filter(vehicle =>
        vehicle.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (vehicleTypeFilter !== 'all') {
      filtered = filtered.filter(vehicle => vehicle.type === vehicleTypeFilter);
    }

    setFilteredVehicles(filtered);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleVehicleTypeChange = (e) => {
    setVehicleTypeFilter(e.target.value);
  };

  return (
    <AppLayout>
      <div className="min-h-screen bg-gray-900">
        {/* Le reste du code de navigation reste identique */}
        <div className="flex">
          {/* ... Code de la sidebar ... */}

          <div className="flex-1 p-6">
            {activeTab === 'vehicles' && (
              <div className="space-y-6">
                {/* ... Code des boutons et filtres ... */}

                <div className="bg-gray-800 rounded-lg overflow-hidden">
                  <table className="w-full">
                    <thead>
                      <tr className="bg-gray-700">
                        <th className="px-6 py-3 text-left">ID</th>
                        <th className="px-6 py-3 text-left">Nom</th>
                        <th className="px-6 py-3 text-left">Type</th>
                        <th className="px-6 py-3 text-left">Prix</th>
                        <th className="px-6 py-3 text-left">Stock</th>
                        <th className="px-6 py-3 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-700">
                      {filteredVehicles.map((vehicle) => (
                        <tr key={vehicle.id}>
                          <td className="px-6 py-4">{vehicle.id}</td>
                          <td className="px-6 py-4">{vehicle.name}</td>
                          <td className="px-6 py-4">{vehicle.type}</td>
                          <td className="px-6 py-4">{vehicle.price} €</td>
                          <td className="px-6 py-4">{vehicle.stock}</td>
                          <td className="px-6 py-4">
                            <div className="flex space-x-2">
                              <button 
                                className="p-2 hover:bg-gray-700 rounded" 
                                onClick={() => handleEditVehicleClick(vehicle)}
                              >
                                <Edit className="h-4 w-4" />
                              </button>
                              <button 
                                className="p-2 hover:bg-gray-700 rounded"
                                onClick={() => handleDeleteVehicle(vehicle.id)}
                              >
                                <Trash2 className="h-4 w-4" />
                              </button>
                              <button className="p-2 hover:bg-gray-700 rounded">
                                <FileText className="h-4 w-4" />
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                {isFormOpen && (
                  <VehicleForm 
                    isOpen={isFormOpen} 
                    onClose={handleFormClose} 
                    onSubmit={handleFormSubmit} 
                    vehicle={currentVehicle}
                  />
                )}
              </div>
            )}

            {activeTab === 'orders' && <OrderManagement />}
            {activeTab === 'clients' && <ClientManagement />}
            {activeTab === 'settings' && (
              <div className="space-y-6">
                <h2 className="text-2xl font-bold">Paramètres</h2>
              </div>
            )}
          </div>
        </div>
      </div>
    </AppLayout>
  );
};

export default AdminDashboard;
