import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const VehicleDetailPage = () => {
  const { id } = useParams(); // Récupérer l'ID du véhicule depuis l'URL
  const [vehicle, setVehicle] = useState(null);

  useEffect(() => {
    // Chercher les détails du véhicule avec l'ID
    const fetchVehicleDetails = () => {
      // Remplacez par votre logique pour récupérer les données du véhicule (ex : API)
      const vehicleData = {
        1: {
          name: 'Tesla Model 3',
          type: 'automobile',
          energy: 'electric',
          price: 42900,
          image: '/api/placeholder/400/300',
          description: 'Berline électrique performante',
        },
        2: {
          name: 'Vespa Elettrica',
          type: 'scooter',
          energy: 'electric',
          price: 7499,
          image: '/api/placeholder/400/300',
          description: 'Scooter électrique urbain',
        },
      };

      setVehicle(vehicleData[id]); // Utiliser l'ID pour obtenir les détails spécifiques
    };

    fetchVehicleDetails();
  }, [id]);

  if (!vehicle) {
    return <div>Chargement...</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <div className="bg-gray-800 p-6 rounded-lg shadow-lg">
        <img
          src={vehicle.image}
          alt={vehicle.name}
          className="w-full h-64 object-cover mb-6"
        />
        <h2 className="text-3xl font-semibold text-white mb-4">{vehicle.name}</h2>
        <p className="text-gray-300 mb-4">{vehicle.description}</p>
        <div className="flex gap-4 mb-6">
          <span className="px-3 py-1 bg-gray-700 rounded-full text-sm text-white">
            {vehicle.type}
          </span>
          <span className="px-3 py-1 bg-gray-700 rounded-full text-sm text-white">
            {vehicle.energy}
          </span>
        </div>
        <div className="flex justify-between items-center">
          <span className="text-blue-400 font-bold text-xl">
            {vehicle.price.toLocaleString()} €
          </span>
          <button className="px-6 py-2 bg-blue-600 rounded-md hover:bg-blue-700 transition-all duration-300">
            Acheter
          </button>
        </div>
      </div>
    </div>
  );
};

export default VehicleDetailPage;
