import React, { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent, Button, Input } from './ui';
import { Mail, Lock, Eye, EyeOff, Google, Facebook } from 'lucide-react';
import { useAuth } from './AuthProvider';
import AppLayout from './app-layout'; // Assure-toi que le chemin est correct

const LoginPage = () => {
  const { login } = useAuth();
  const [showPassword, setShowPassword] = useState(false);
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await login(credentials);
      window.location.href = '/dashboard'; // Redirection après connexion
    } catch (error) {
      setError('Email ou mot de passe incorrect');
    } finally {
      setLoading(false);
    }
  };

  return (
    <AppLayout> {/* Ajout de AppLayout */}
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-indigo-600 to-purple-800 px-4">
        <Card className="w-full max-w-md rounded-lg shadow-lg">
          <CardHeader>
            <CardTitle className="text-center text-white text-2xl font-semibold">Connexion</CardTitle>
          </CardHeader>
          <CardContent>
            {error && <div className="mb-4 p-3 bg-red-500/20 border border-red-500 rounded-lg text-red-500">{error}</div>}

            <form onSubmit={handleLogin} className="space-y-6">
              {/* Email */}
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Email</label>
                <div className="relative">
                  <Input
                    type="email"
                    placeholder="nom@exemple.com"
                    value={credentials.email}
                    onChange={(e) => setCredentials({ ...credentials, email: e.target.value })}
                    className="pl-10"
                  />
                  <Mail className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                </div>
              </div>

              {/* Password */}
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Mot de passe</label>
                <div className="relative">
                  <Input
                    type={showPassword ? 'text' : 'password'}
                    placeholder="Votre mot de passe"
                    value={credentials.password}
                    onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
                    className="pl-10 pr-12"
                  />
                  <Lock className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-2.5 text-gray-400 hover:text-gray-300"
                  >
                    {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
                  </button>
                </div>
              </div>

              {/* Remember Me */}
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={() => setRememberMe(!rememberMe)}
                  className="h-5 w-5 text-indigo-500 rounded"
                />
                <span className="text-sm text-gray-300">Se souvenir de moi</span>
              </div>

              {/* Submit Button */}
              <Button type="submit" variant="primary" className="w-full" disabled={loading}>
                {loading ? 'Connexion...' : 'Se connecter'}
              </Button>

              {/* Social Media Login */}
              <div className="flex items-center space-x-4 pt-4">
                <Button variant="outline" className="w-full flex items-center justify-center space-x-2">
                  <Google className="h-5 w-5 text-gray-700" />
                  <span>Se connecter avec Google</span>
                </Button>
                <Button variant="outline" className="w-full flex items-center justify-center space-x-2">
                  <Facebook className="h-5 w-5 text-blue-600" />
                  <span>Se connecter avec Facebook</span>
                </Button>
              </div>

              {/* Forgot Password Link */}
              <div className="text-center text-sm text-gray-300 mt-4">
                <a href="/forgot-password" className="hover:underline">Mot de passe oublié ?</a>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </AppLayout>
  );
};

export default LoginPage;
