import React, { useState } from 'react';
import { useLocation } from 'react-router-dom'; // Utilisation de useLocation pour obtenir l'état
import AppLayout from './app-layout';

const OrderForm = () => {
  const location = useLocation();
  const { cart } = location.state || {}; // On récupère les données du panier envoyées depuis ShoppingCart

  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    postalCode: '',
    country: 'FR',
    creditCardNumber: '',
    creditCardExpiry: '',
    creditCardCVC: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Commande soumise:', formData);
  };

  return (
    <AppLayout>
      <div className="max-w-4xl mx-auto space-y-8">
        <h2 className="text-2xl font-bold">Finaliser votre commande</h2>

        {/* Liste des produits commandés */}
        <div className="bg-gray-800 rounded-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Vos commandes</h3>
          <div className="space-y-4">
            {cart && cart.length > 0 ? (
              <ul className="space-y-2">
                {cart.map((item, index) => (
                  <li key={index} className="flex justify-between items-center">
                    <span>{item.name}</span>
                    <span>{(item.price * item.quantity).toLocaleString()} €</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p>Aucune commande ajoutée.</p>
            )}
          </div>
        </div>

        {/* Formulaire de commande et autres sections restent inchangés */}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Informations personnelles, méthode de paiement, etc. */}
        </form>
      </div>
    </AppLayout>
  );
};

export default OrderForm;
