import React from 'react';
import { useParams } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import AppLayout from './app-layout';

const VehicleDetailPage = () => {
  const { id } = useParams(); // On récupère l'ID du véhicule à partir de l'URL

  // Exemple de données statiques pour un véhicule. Dans un cas réel, ces données viendraient d'une API.
  const vehicle = {
    id,
    name: `Véhicule Modèle ${id}`,
    description: 'Description détaillée du véhicule, incluant ses caractéristiques techniques, ses performances et ses fonctionnalités.',
    price: '29,999 €',
    image: `/api/placeholder/800/600`,
    features: [
      'Moteur 200 CV',
      'Transmission automatique',
      'Jantes en alliage',
      'Système de navigation',
      'Caméra de recul'
    ]
  };

  return (
    <AppLayout>
      <div className="space-y-12 py-12">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-blue-400 mb-4">{vehicle.name}</h1>
          <img
            src={vehicle.image}
            alt={vehicle.name}
            className="w-full max-w-2xl mx-auto mb-6 rounded-lg shadow-lg object-cover"
          />
          <p className="text-xl text-gray-300 mb-6">{vehicle.description}</p>
          <div className="text-xl font-semibold text-blue-400 mb-6">{vehicle.price}</div>
        </div>

        <div className="space-y-4">
          <h2 className="text-2xl font-bold text-blue-400">Caractéristiques</h2>
          <ul className="list-disc pl-5 space-y-2 text-gray-300">
            {vehicle.features.map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul>
        </div>

        <div className="mt-8 text-center">
          <a
            href="/catalogue"
            className="inline-flex items-center px-6 py-3 text-base font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
          >
            <ChevronLeft className="mr-2 h-5 w-5" />
            Retour au catalogue
          </a>
        </div>
      </div>
    </AppLayout>
  );
};

export default VehicleDetailPage;
